INSERT INTO `draft_courses` VALUES ('2284', '773317', 'TOXI', '491', '2006', 'SEMINAR', 'Students are required to make oral presentations and discuss special current problems in the field of toxicology. Involves presentation and class participation. A final comprehensive examination will cover all aspects of the discipline of toxicology. Prerequisite: credit or registration in all required Toxicology courses. Toxicology majors only. F,Sp', '1', '1', '0', '0', '', '0');
