INSERT INTO `draft_courses` VALUES ('58495', '586405', 'BIOL', '208', '2006', 'Animal Diversity', 'The anatomy, physiology, and classification of animals with an emphasis on animal diversity. Prerequisites: &quot;C&quot; or better in 120, 121.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2824', '888749', 'CSCI', '163', '2006', 'COMPUTER SCIENCE FOR THE USER', 'A study of computers, how and why they work, their uses in modern society, and their impact on that society. Also included are introductions to common software packages and computer programming. This course is not applicable toward a degree in Computer Science or in the College of Business Administration.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2825', '231453', 'CSCI', '167', '2006', 'SOFTWARE APPLICATIONS', 'Work with three major computer application software packages - word processing, spreadsheets, and databases. Not applicable towards a degree in Computer Science or in the College of Business Administration.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('4960', '47988', 'RELG', '207', '2006', 'INTRODUCTION TO THE BIBLE: OLD TESTAMENT', 'The history, literature, teachings, and philosophical concepts of the Old Testament as they relate to the development of religious and ethical ideas of contemporary thought; selected readings and passages from the books of history, law, the prophets, and other writings.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('4961', '867031', 'RELG', '208', '2006', 'INTRODUCTION TO THE BIBLE: NEW TESTAMENT', 'The history, literature, teachings, and philosophical concepts of the New Testament as they relate to the development of religious and ethical ideas of contemporary thought; emphasis upon the Gospel accounts of the life and teachings of Jesus, the development and spread of Christianity as reflected in the writings of Paul and others.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13268', '592679', 'CMST', '260', '2006', 'Small Group Communication', 'Introduction to small group participation and leadership. Small group variables included for study are group structure, climate, goals, roles, norms, conflict, and rhetorical sensitivity. Special emphasis given to application of concepts within problem-solving discussion groups.', '3', '3', '3', '0', '', '0');
