INSERT INTO `draft_courses` VALUES ('58661', '468798', 'ACCT', '110', '2006', 'Survey', 'Fundamental principles and managerial applications; data processing. Designed to meet the needs of students majoring in subjects outside the College of Business Administration. Not open to majors in the College of Business\r\nAdministration. F, Sp', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58662', '553209', 'H&HP', '417', '2006', 'School Health Problems', 'School health problems and current programs of prevention and protection against disease. Prerequisite: Junior standing. (Formerly Health and Physical Education 417). Education majors only.', '3', '3', '3', '0', '', '0');
