INSERT INTO `draft_courses` VALUES ('13575', '209018', 'H&HP', '201', '2006', 'First Aid And CPR', 'First aid to the victims of accidents, sudden illness, cardiac or respiratory emergencies. First aid and CPR certification granted to those meeting agency requirements. Two hours laboratory. (Formerly First Aid and Personal Safety.)', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13582', '60759', 'H&HP', '211', '2006', 'Foundations Of Physical Education', 'Basic understanding and familiarization of health, physical education and recreation as a profession; including historical background, principles, trends, curriculum and professional preparation. For Health and Physical Education majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13588', '90860', 'H&HP', '220', '2006', 'Lifeguard Training', 'Designed to train and certify an individual to serve as a non-surf lifeguard and as an aquatic facility manager.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13594', '730398', 'H&HP', '221', '2006', 'Concepts Of Personal Wellness', 'The purpose of this course is to provide learning opportunities for the development of conceptual and functional knowledge of health-related physical fitness and the role it plays in the development and maintenance of human wellness. The health-behavior-gap is examined, and specific emphasis is placed on the individual long-term preventive and rehabilitative benefits derived from lifelong participation in physical activity. Prerequisite: Kinesiology majors only.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13600', '394389', 'H&HP', '233', '2006', 'Water Safety Instruction', 'Qualifies individuals to teach and certify American Red Cross Aquatic courses. Prerequisite: 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13606', '440761', 'H&HP', '241', '2006', 'Personal And Community Health', 'An orientation course in health which deals with nutrition, stress, exercise, diseases and their prevention.', '3', '3', '3', '0', '', '0');
