INSERT INTO `draft_courses` VALUES ('13618', '310064', 'H&HP', '248', '2006', 'Officiating', 'Principles, techniques and practices of officiating with emphasis on rules, rule interpretations and mechanics of basketball and football. Practical experience will be arranged.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13624', '566603', 'H&HP', '249', '2006', 'Officiating Spring Sports', 'This course is designed to enhance the student’s ability to understand the basic principles, techniques, and practices of officiating youth sports with emphasis on rules, rule interpretations, and mechanics for Dixie League softball/baseball, FIFA U6-U19 soccer, and USTA tennis.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13636', '305606', 'H&HP', '256', '2006', 'Living With Health', 'Content and theory related to basic health information; critical health issues; improving and maintaining optimal health and wellness. Students with prior credit in Kinesiology 241 are not eligible to receive credit in Kinesiology 256. Cannot be used to satisfy major or minor requirements.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13642', '398321', 'H&HP', '301', '2006', 'Coaching Football', 'Theory, techniques, and skills of coaching football. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13648', '693551', 'H&HP', '302', '2006', 'Coaching Basketball', 'Theory, techniques, and skills of coaching basketball. Prerequisites: KINS 211 and junior standing. INSERT INTO `draft_courses` VALUES ('13654', '890802', 'H&HP', '303', '2006', 'Coaching Baseball And Softball', 'Theory, techniques, and skills of coaching baseball and softball. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13660', '759876', 'H&HP', '304', '2006', 'Coaching Track And Field', 'Theory, techniques, and skills of coaching track and field. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13666', '884610', 'H&HP', '305', '2006', 'Techniques In Group Exercise', 'This course is designed to examine current trends and techniques used in the field of group exercise. Prerequisite: KINS 211. Kinesiology majors only.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13672', '146706', 'H&HP', '318', '2006', 'Fitness Assessment Techniques', 'This course is designed to provide the exercise professional with the necessary cognitive and laboratory experiences essential for developing safe and accurate physical fitness testing practices. Prerequisite: KINS 211. Kinesiology majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13678', '367416', 'H&HP', '325', '2006', 'Driver Education And Traffic Safety', 'Administrative problems and policies, instructional methods and materials, accident causes and prevention, and content of a high school course in driver education. Prerequisite: Junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13684', '163489', 'H&HP', '333', '2006', 'Activities For Elementary School', 'Materials, techniques and methods of teaching physical education in elementary schools. Sophomore level and above.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13690', '906730', 'H&HP', '335', '2006', 'TEAM SPORTS', 'Basic skills, strategies, rules, and teaching procedures of selected team sports. Prerequisite: Health and physical education major.', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13696', '969837', 'H&HP', '402', '2006', 'Exercise Program Design', 'This course is designed to apply the principles of exercise leadership to clinical and preventive settings, with particular emphasis on formulation of the exercise prescription. Prerequisite: KINS 318. KINS majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13702', '492451', 'H&HP', '406', '2006', 'Advanced Driver Education', 'Curriculum innovations and instructional devices (with lab). In-depth study of curriculum materials and instructional devices and techniques including simulation, multi-media, driving range, on-street instruction, and motorcycle. Instruction in the use of all forms supplied by the State Department of Education. Prerequisites: KINS 325 and junior standing.', '4', '4', '4', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13708', '261290', 'H&HP', '408', '2006', 'Physical Activity For Students With Special Needs', 'This course examines how physical activities and motor skills can be tasked analyzed and modified to include a wide range of individuals with special needs. Basic knowledge, skills and competencies in Special Education are covered with an emphasis in pedagogical principles.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13714', '52273', 'H&HP', '411', '2006', 'Athletic Injuries', 'The prevention and emergency care of athletic injuries. Prerequisites: KINS 211 and junior standing. Kinesiology majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13720', '240201', 'H&HP', '413', '2006', 'Management Of Health, Physical Education, And Recr', 'The purpose and functions of the management process in the instructional and related area programs in today’s classroom as well as experiences in those areas outside the normal classroom setting. Federal and state regulations in education, alternative programs, discipline and other current problems are discussed. Ten (10) hours of classroom observation will be completed during this course. Prerequisite: KINS 211 and Junior standing. (Formerly Administration and Health, Physical Education, and Recreation.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58664', '633125', 'H&HP', '419', '2006', 'Modern Fitness', 'Lectures and exercises designed to convert deconditioned people to conditioned status with sufficient basic knowledge to remain conditioned for a lifetime. Prerequisite: Junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58666', '65599', 'H&HP', '426', '2006', 'Safety Education', 'Enhances the understanding of safety and delivery of age-appropriate safety education. Prerequisite: KINS 241. (Formerly Health and Human Performance 426).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58672', '970598', 'H&HP', '431', '2006', 'Methods And Materials In Health Education', 'Materials, techniques, and methods of teaching health in schools. Ten (10) hours of classroom observation will be completed during this course. Prerequisite: Must be admitted to teacher education.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58674', '947598', 'H&HP', '432', '2006', 'Anatomical And Physiological Kinesiology', 'Application of anatomy and physiology to analysis and improvement of human performance in gross body movement. Prerequisite: Junior standing and KINS 211. KINS majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58676', '640330', 'H&HP', '435', '2006', 'Practicum In Physical Education (general)', 'Observation and participation in non-teaching physical education, recreational or health related programs for one semester. Prerequisite: Health and physical education major (general).', '6', '6', '6', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58678', '792910', 'H&HP', '437', '2006', 'Physiology Of Exercise', 'Acute and chronic physiological changes in response to exercise. Emphasis on the practical application of exercise training for health, fitness, and- performance. Prerequisite: Junior standing. Kinesiology majors only. Concurrent enrollment in KINS 438.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58680', '109444', 'H&HP', '438', '2006', 'Exercise Physiology Laboratory', 'A laboratory course to accompany KINS 437. Emphasis is given to demonstration of physiological responses to exercise testing, as well as other laboratory procedures unique to exercise science. Prerequisite: Credit for or concurrent enrollment in KINS 437. Kinesiology majors only.', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58682', '514201', 'H&HP', '441', '2006', 'Worksite Health Promotion', 'This course is designed to develop knowledge and awareness of the major issues in the field of worksite health promotiong programming, as well as to enhance the ability of future practitioners to develop the professional techniques necessary to fulfill wellness programming needs within corporate fitness/wellness field. Prerequisites: KINS 211, 241 and junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58684', '194376', 'H&HP', '442', '2006', 'Health And Physical Education Program Development', 'This course is designed to foster the examination, discussion and creation of health physical education program development. Prerequisite: Permission of instructor. Education majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13788', '344414', 'CHEM', '303', '2006', 'Organic Chemistry I', 'Discussion of structure and bonding theory, acid-base chemistry, reactions of alkanes, alkenes, alkynes, alcohols and alkyl halides. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 1008. F, S, Su1 (Formerly 303)', '3', '3', '3', '0', '', '0');
