INSERT INTO `draft_courses` VALUES ('14278', '566877', 'AVIA', '415', '2006', 'Aircraft Engines And Systems', 'Advanced aircraft engines and systems of a typical large twin pressurized turboprop, including warning systems, doors, electrical, lighting, fuel, engines, fire protection, hydraulics, flight controls, stall warnings, avionics, TCAS, EGPWS, communications, landing gear, pneumatics, air conditioning, pressurization, oxygen, ice protection, APU, and emergency equipment. Prerequisite: minimum grade of &quot;C&quot; in 110 and 111, completion of all 100-level English and Math courses, or instructor approval. (Formerly 416)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14284', '698562', 'ACCT', '440', '2006', 'Law for Accountants', 'Specifically for accounting majors. Legal concepts underlying business organizations, contracts, debtor-creditor relationships, government regulation of business, the uniform commercial code, property and professional and legal responsibilities. Can be used as a preparation for the CPA examination. Prerequisite: 401. F, Sp (Formerly ACCT 440.)', '3', '3', '3', '0', '', '0');
