INSERT INTO `draft_courses` VALUES ('14308', '404242', 'CHEM', '336', '2006', 'Biochemistry I', 'Introduction to structure and function of proteins, nucleic acids, lipids and carbohydrates and enzyme kinetics, mechanisms, regulation, and vitamins. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 232. F, Su1 (Formerly 336)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14317', '544549', 'SPLP', '113', '2006', 'Intro/splh Disorders', 'For SPLP majors in the College of Health Sciences, for Health Care Professionals, and for Education majors.  A survey of speech, language and hearing development and associated disorders.  An introduction to the professions of Speech-Language Pathology and Audiology.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14467', '881028', 'SOCL', '495F', '2006', 'Qualitative Research', 'An examination of different types of qualitative research, techniques of data collection, analysis, and presentation, and ethical issues; exploration of the strengths and limitations of this type of interpretive work. (Formerly SOCL 495F.) (Same as SOCL 452.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14386', '751975', 'PHIL', '403', '2006', 'Ethics', 'A study of the theoretical foundations of morality with reference to approaches to ethical thought, character, and action. Concepts include good and evil, right and wrong, obligation and rights, responsibility and freedom. The application of these concepts to the working out of particular ethical problems. (Same as PHIL 403.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14350', '839632', 'ANTS', '233', '2006', 'Forensics', 'An overview of techniques applied in identification of human skeletal remains for the medicolegal professions, including assessment of age, sex, ancestry, trauma, and disease; occupational stress markers; procedures for search/recovery of human remains; and estimations of time since death (same as ANTS and SOCL 233).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14362', '950064', 'FCSC', '219', '2006', 'FCSC 219', 'There is no course description available at this time.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14402', '893304', 'SPLP', '222', '2006', 'American Sign Language II', 'This course is designed to expand receptive and expressive American Sign Language (ASL) skills. It is a natural language approach incorporating cultural interactions which are being used to develop appropriate conversational skills in ASL at the survival-plus level. Vocabulary acquisition and the understanding and use of ASL linguistic structure is emphasized. Instruction is in ASL without voice. Prerequisite: SPLP 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14408', '325076', 'SPLP', '220', '2006', 'Beginning Sign Language', 'An introductory course in the study of American Sign Language (ASL) and Pidgin Sign English (PSE) with an emphasis on the Deaf Community and their culture. An understanding of some of the basic cultural differences of the Deaf and how they relate to the development and existence of sign languages will be coupled with basic skills in both ASL and PSE. Non-majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14414', '256146', 'SPLP', '152', '2006', 'Speech And Language Acquisition', 'The development of speech in children in the family and the effects of culture on language.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14420', '268561', 'SPLP', '151', '2006', 'General Foundation In Audiology', 'The nature of deafness and hard of hearing problems of children.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14426', '183996', 'POLS', '471', '2006', 'Social Statistics', 'Statistical procedures appropriate to the measurement and analysis of socal data. (Same as SOCL 451.) Prerequisite: POLS 101', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14441', '636211', 'SOCL', '464', '2006', 'Internship In Sociology', 'Field experience under the general supervision of a faculty member and the direct supervision of a professional in the field, with academic components including a final paper and presentation. Prerequisite: SOCL 203. (Same as POLS 464.)', '3', '6', '6', '0', '', '0');
