INSERT INTO `draft_courses` VALUES ('14488', '801043', 'SPLP', '300', '2006', 'Supervised Observation In Communicative Disorders', 'Students observes 25 hours of diagnostic and therapeutic sessions with communicatively impaired individuals in approved settings. Prerequisiste: Permission of instructor.', '2', '2', '2', '0', '', '0');
