INSERT INTO `draft_courses` VALUES ('14512', '75925', 'SPLP', '426', '2006', 'Diagnosis And Appraisal Of Communication Disorders', 'Instruments, procedures, and report writing skills utilized in diagnosing and reporting speech, language, and hearing disorders. Enrollment in 427 is required. Prerequisite: 113 and consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14518', '936289', 'SPLP', '427', '2006', 'Diagnosis And Appraisal Of Comm Disorders Lab', 'A practicum developing the skills to administer and score tests of articulation and language. Prerequisite: Credit or registration in 426, or approval of instructor.', '2', '2', '2', '0', '', '0');
