INSERT INTO `draft_courses` VALUES ('14549', '979033', 'SPLP', '436', '2006', 'Manual Communication', 'An introductory course in manual communication of the hearing impaired. Emphasis on drills and exercises to help students acquire a sign vocabulary fluency.', '3', '3', '3', '0', '', '0');
