INSERT INTO `draft_courses` VALUES ('14567', '533578', 'SPLP', '476', '2006', 'Beginning Practicum In Communication Disorders', 'Clinical practicum with individuals who are communicatively challenged. Students will complete the required clock hours with clients having specific disorders as specified by the instructor. Prerequisite: Approval of department head.', '1', '6', '6', '0', '', '0');
